const socket = io();

// Get references to DOM elements
const form = document.getElementById('send-container');
const input = document.getElementById('messageInp');
const container = document.querySelector('.container');

// Add message to chat container
function appendMessage(message, position) {
    const messageElement = document.createElement('div');
    messageElement.classList.add('message', position);
    messageElement.innerText = message;
    container.append(messageElement);
    container.scrollTop = container.scrollHeight; // Auto-scroll to the latest message
}

// Listen for form submit
form.addEventListener('submit', (e) => {
    e.preventDefault(); // Prevent form reload
    const message = input.value;
    appendMessage(`You: ${message}`, 'right'); // Add your own message to chat
    socket.emit('chat message', message); // Send message to server
    input.value = ''; // Clear the input field
});

// Listen for chat messages from server
socket.on('chat message', (message) => {
    appendMessage(message, 'left'); // Add received message to chat
});
